// --- Fonctions de récupération de données ---

export function getTrendingProducts(products: Product[]): Product[] {
  return trendingProductIds
    .map(id => products.find(p => p.id === id))
    .filter((p): p is Product => p !== undefined);
}

export function getProductsByCategory(products: Product[], categorySlug: string, limit?: number, excludeId?: string): Product[] {
  let filteredProducts: Product[];

  if (categorySlug === 'all') {
    filteredProducts = products;
  } else {
    filteredProducts = products.filter((p) => p.category === categorySlug);
  }

  if (excludeId) {
    filteredProducts = filteredProducts.filter((p) => p.id !== excludeId);
  }
  
  if (limit) {
    return filteredProducts.slice(0, limit);
  }

  return filteredProducts.sort((a, b) => a.id.localeCompare(b.id));;
}

export function getProductsBySubcategory(products: Product[], categorySlug: string, subcategorySlug: string, limit?: number, excludeId?: string): Product[] {
  let filteredProducts: Product[];

  // Filtrer d'abord par catégorie
  if (categorySlug === 'all') {
    filteredProducts = products;
  } else {
    filteredProducts = products.filter((p) => p.category === categorySlug);
  }

  // Ensuite filtrer par sous-catégorie
  filteredProducts = filteredProducts.filter((p) => p.subcategory === subcategorySlug);

  if (excludeId) {
    filteredProducts = filteredProducts.filter((p) => p.id !== excludeId);
  }
  
  if (limit) {
    return filteredProducts.slice(0, limit);
  }

  return filteredProducts.sort((a, b) => a.id.localeCompare(b.id));
}

export function getProductBySlug(products: Product[], slug: string): Product | undefined {
  return products.find((p) => p.slug === slug);
}

export function getProductById(products: Product[], id: string): Product | undefined {
  return products.find((p) => p.id === id);
}

export function getFeaturedProducts(products: Product[], limit: number = 4): Product[] {
    const winterProducts = products.filter(p => p.category === 'winter-clothing' || p.name.toLowerCase().includes('winter') || p.name.toLowerCase().includes('mantel') || p.name.toLowerCase().includes('pullover'));
    return winterProducts.sort((a, b) => a.id.localeCompare(b.id)).slice(0, limit);
}

export function getWinterSaleProducts(products: Product[], limit?: number): Product[] {
  const allSaleItems = products.filter(p => p.oldPrice).sort((a, b) => a.id.localeCompare(b.id));

  if (!limit) {
    return allSaleItems;
  }
  
  return allSaleItems.slice(0, limit);
}
